package per.yunfan.cse406.javaee.cookie;import javax.servlet.annotation.WebServlet;import javax.servlet.http.Cookie;import javax.servlet.http.HttpServlet;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import java.io.IOException;@WebServlet("/cookie")public class CookieServlet extends HttpServlet {    @Override    protected void doGet(HttpServletRequest req, HttpServletResponse resp) throws IOException {        Cookie cookie = new Cookie("CID", "One");		resp.addCookie(cookie);		resp.getWriter().println("Added cookie: (CID, One)");    }    @Override    protected void doPost(HttpServletRequest req, HttpServletResponse resp) throws IOException {        this.doGet(req, resp);    }}