package per.yunfan.cse406.javaee.session;import javax.servlet.annotation.WebServlet;import javax.servlet.http.HttpServlet;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import javax.servlet.http.HttpSession;import java.io.IOException;import java.io.PrintWriter;/** * 读取Session写入Demo的Servlet */@WebServlet("/sessionRead")public class SessionReadServlet extends HttpServlet {    @Override    protected void doGet(HttpServletRequest req, HttpServletResponse resp) throws IOException {	    HttpSession session = req.getSession(false);		PrintWriter out = resp.getWriter();		if(session != null) {			String userName = (String)session.getAttribute("USER");			if(userName != null) {				out.println("Welcome user: "+ userName);				return;			}		}		out.println("Anonymous user visit");    }    @Override    protected void doPost(HttpServletRequest req, HttpServletResponse resp) throws IOException {		this.doGet(req, resp);    }}