package per.yunfan.cse406.javaee.session;import javax.servlet.annotation.WebServlet;import javax.servlet.http.HttpServlet;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import javax.servlet.http.HttpSession;import java.io.IOException;/** * 写入Session的Demo */@WebServlet("/session")public class SessionServlet extends HttpServlet {    @Override    protected void doGet(HttpServletRequest req, HttpServletResponse resp) throws IOException {		this.doPost(req, resp);    }    @Override    protected void doPost(HttpServletRequest req, HttpServletResponse resp) throws IOException {		String username = req.getParameter("user");        String password = req.getParameter("pwd");		HttpSession session = req.getSession(true);		session.setAttribute("USER", username);		resp.getWriter().println("Welcome, " + username);       }}